package pgp

import (
	"encoding/base64"
	"strings"
)

func b64Dec(str string) string {
	str = strings.Replace(str, " ", "", -1)
	s, err := base64.StdEncoding.DecodeString(str)
	if err != nil {
		panic(err)
	}

	return string(s)
}

